import { ADD_FAV, REMOVE_FAV } from "./action-tipes";

const inicialState = {
  myFavorite: [],
  allCharacters: [],
};

const reducer = (state = inicialState, { type, payload }) => {
  switch (type) {
    case ADD_FAV:
      return { ...state,
               myFavorites: payload,
               allCharacters: payload };

    case REMOVE_FAV:
      return { ...state,
               myFavorites: payload };
    default:
      return { ...state };
  }
};

export default reducer;
